import { useEffect, useState } from 'react';
import { getLiveMatches, getMatchesByDate } from '../api/matchService';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState('live');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        let data;
        if (activeTab === 'live') {
          data = await getLiveMatches();
        } else {
          const date = activeTab === 'recent'
            ? new Date().toISOString().split('T')[0]
            : new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().split('T')[0];
          data = await getMatchesByDate(date);
        }
        setMatches(data);
      } catch (error) {
        console.error('Failed to fetch matches:', error);
      }
    };

    fetchMatches();
  }, [activeTab]);

  return (
    <div>
      <h2>üèè {activeTab === 'live' ? 'Live Matches' : activeTab === 'recent' ? 'Recent Matches' : 'Upcoming Matches'}</h2>
      <div>
        <button onClick={() => setActiveTab('live')}>Live Matches</button>
        <button onClick={() => setActiveTab('recent')}>Recent Matches</button>
        <button onClick={() => setActiveTab('upcoming')}>Upcoming Matches</button>
      </div>
      {matches.map((match) => (
        <div key={match._id}>
          <p>{match.league} - {match.teamA} vs {match.teamB}</p>
          <button onClick={() => navigate(`/match/${match._id}`)}>Match Details</button>
          <button onClick={() => navigate(`/betting/${match._id}`)}>Bet Now</button>
        </div>
      ))}
    </div>
  );
};

export default HomePage;
