import React, { useEffect, useState } from "react";
import axios from "axios";
import MatchCard from "../components/MatchCard";

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState("current");

  useEffect(() => {
    const today = new Date().toISOString().split("T")[0];

    axios.get(`http://localhost:5001/api/matches/${today}`)
      .then(res => setMatches(res.data))
      .catch(err => console.error("Error fetching matches", err));
  }, []);

  return (
    <div>
      <h1>Live Matches</h1>

      {/* Tabs for filtering matches */}
      <div className="tabs">
        <button onClick={() => setActiveTab("current")}>Current</button>
        <button onClick={() => setActiveTab("recent")}>Recent</button>
        <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>
      </div>

      {/* Display Matches */}
      <div className="match-list">
        {matches.length === 0 ? (
          <p>No matches available.</p>
        ) : (
          matches
            .filter(match => {
              if (activeTab === "current") return match.status === "live";
              if (activeTab === "recent") return match.status === "ended";
              if (activeTab === "upcoming") return match.status === "scheduled";
              return true;
            })
            .map(match => <MatchCard key={match.id} match={match} />)
        )}
      </div>
    </div>
  );
};

export default Home;
