import React, { useState } from "react";

const Tabs = ({ matchDetails, bettingSection }) => {
    const [activeTab, setActiveTab] = useState("info");

    return (
        <div className="container mt-4">
            <ul className="nav nav-tabs">
                <li className="nav-item">
                    <button className={`nav-link ${activeTab === "info" ? "active" : ""}`} onClick={() => setActiveTab("info")}>
                        Match Info
                    </button>
                </li>
                <li className="nav-item">
                    <button className={`nav-link ${activeTab === "score" ? "active" : ""}`} onClick={() => setActiveTab("score")}>
                        Live Score
                    </button>
                </li>
                <li className="nav-item">
                    <button className={`nav-link ${activeTab === "betting" ? "active" : ""}`} onClick={() => setActiveTab("betting")}>
                        Betting
                    </button>
                </li>
            </ul>

            <div className="tab-content mt-3">
                {activeTab === "info" && (
                    <div>
                        <h3>Match Info</h3>
                        <p><strong>Match Name:</strong> {matchDetails.name}</p>
                        <p><strong>Status:</strong> {matchDetails.status}</p>
                    </div>
                )}

                {activeTab === "score" && (
                    <div>
                        <h3>Live Score</h3>
                        <p>{matchDetails.display_score || "Not Available"}</p>
                    </div>
                )}

                {activeTab === "betting" && bettingSection}
            </div>
        </div>
    );
};

export default Tabs;
