import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details");
                console.error("‚ùå Error fetching match details:", err);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    return (
        <div className="match-details">
            <h1>Match Details</h1>
            <h2>{matchDetails.teams ? matchDetails.teams.join(" vs ") : "Teams Unavailable"}</h2>
            <p><strong>League:</strong> {matchDetails.league}</p>
            <p><strong>Venue:</strong> {matchDetails.venue || "Unknown"}</p>
            <p><strong>Scheduled:</strong> {matchDetails.scheduled}</p>
            <p><strong>Status:</strong> {matchDetails.status}</p>
            <p><strong>Toss Winner:</strong> {matchDetails.toss_winner} ({matchDetails.toss_decision})</p>
            <p><strong>Match Winner:</strong> {matchDetails.winner || "N/A"}</p>

            <h3>Live Commentary:</h3>
            {matchDetails.commentary && matchDetails.commentary.length > 0 ? (
                <ul>
                    {matchDetails.commentary.map((event, index) => (
                        <li key={index}>
                            [{event.time}] {event.description}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No commentary available.</p>
            )}
        </div>
    );
};

export default MatchPage;
