import React, { useState } from "react";
import MatchDetails from "./MatchDetails";

const Tabs = ({ matchDetails, timeline }) => {
  const [activeTab, setActiveTab] = useState("scorecard");

  return (
    <div>
      <div className="tabs">
        <button onClick={() => setActiveTab("scorecard")}>Scorecard</button>
        <button onClick={() => setActiveTab("commentary")}>Commentary</button>
        <button onClick={() => setActiveTab("details")}>Match Details</button>
      </div>

      {activeTab === "scorecard" && <MatchDetails matchDetails={matchDetails} />}
      {activeTab === "commentary" && (
        <div>
          <h3>Ball-by-Ball Commentary</h3>
          {timeline.length === 0 ? (
            <p>No commentary available.</p>
          ) : (
            <ul>
              {timeline.map((event, index) => (
                <li key={index}>{event.description}</li>
              ))}
            </ul>
          )}
        </div>
      )}
      {activeTab === "details" && (
        <div>
          <h3>Match Information</h3>
          <p>Tournament: {matchDetails.sport_event.tournament.name}</p>
          <p>Venue: {matchDetails.sport_event.venue?.name || "N/A"}</p>
          <p>Scheduled Date: {new Date(matchDetails.sport_event.scheduled).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
};

export default Tabs;
