import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);
    const [selectedTab, setSelectedTab] = useState("summary");

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                console.error("❌ Error fetching match details:", err);
                setError("Failed to load match details.");
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    return (
        <div>
            <h1>Match Details</h1>
            <div className="tabs">
                <button onClick={() => setSelectedTab("summary")}>Summary</button>
                <button onClick={() => setSelectedTab("scorecard")}>Scorecard</button>
                <button onClick={() => setSelectedTab("commentary")}>Commentary</button>
            </div>

            {error && <p>{error}</p>}
            {matchDetails && (
                <div>
                    <h2>{matchDetails.match_name}</h2>
                    <p>Winner: {matchDetails.winner?.name || "TBD"}</p>
                </div>
            )}
        </div>
    );
};

export default MatchPage;
