import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchMatches(new Date().toISOString().split("T")[0]);
  }, []);

  const fetchMatches = async (date) => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5001/api/matches/${date}`);
      console.log("Fetched Matches:", res.data); // Debugging Log
      setMatches(res.data.results || []); // Ensure correct array is set
      setError("");
    } catch (err) {
      console.error("Error fetching matches:", err);
      setError("Failed to fetch matches. Please try again.");
    }
    setLoading(false);
  };

  return (
    <div>
      <h1>Live Matches</h1>
      <div>
        <button onClick={() => fetchMatches(new Date().toISOString().split("T")[0])}>Current</button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() - 1)).toISOString().split("T")[0])}>Recent</button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().split("T")[0])}>Upcoming</button>
      </div>

      {loading && <p>Loading matches...</p>}
      {error && <p>{error}</p>}
      {matches.length > 0 ? (
        matches.map((match) => (
          <div key={match.sport_event.id} className="match-container">
            <Link className="match-link" to={`/match/${match.sport_event.id}`}>
              <h2>{match.sport_event.competitors[0].name} vs {match.sport_event.competitors[1].name}</h2>
            </Link>
            <p>Status: {match.sport_event_status.status}</p>
            <p>Winner: {match.sport_event_status.winner_id ? match.sport_event_status.winner_id : "TBD"}</p>
          </div>
        ))
      ) : (
        !loading && <p>No matches available.</p>
      )}
    </div>
  );
};

export default Home;
