import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./MatchPage.css";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                console.log("ðŸŸ¢ [FRONTEND] Match Details received:", response.data);
                setMatchDetails(response.data);
            } catch (err) {
                console.error("ðŸ”´ [ERROR] Could not fetch match details.", err);
                setError("Failed to load match details.");
            }
        };
        fetchMatchDetails();
    }, [matchId]);

    if (!matchDetails) return <h2>Loading match details...</h2>;
    if (error) return <h2>{error}</h2>;

    return (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.sport_event.competitors[0]?.name} vs {matchDetails.sport_event.competitors[1]?.name}</h2>
            <p>League: {matchDetails.sport_event.tournament.name}</p>
            <p>Scheduled: {new Date(matchDetails.sport_event.scheduled).toLocaleString()}</p>
            <p>Status: {matchDetails.sport_event_status.status}</p>
            <p>Winner: {matchDetails.sport_event_status.winner_id}</p>
            <p>Toss: {matchDetails.sport_event_status.toss_won_by} chose to {matchDetails.sport_event_status.toss_decision}</p>
        </div>
    );
};

export default MatchPage;
