import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMatchDetails } from '../api/apiService';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';
import '../styles/pages/MatchDetailsPage.css';

const MatchDetails = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const data = await getMatchDetails(matchId);
        setMatch(data);
      } catch (error) {
        console.error('Failed to fetch match details:', error);
      }
    };

    if (matchId) fetchMatchDetails();
  }, [matchId]);

  if (!match) return <p>Loading match details...</p>;

  return (
    <div className="match-details-container">
      {/* ‚úÖ Match Info */}
      <h2>{match.league}</h2>
      <p>üèü Venue: {match.venue}</p>
      <p>üìÖ Date: {new Date(match.scheduled).toLocaleString()}</p>
      <p>üî• Status: {match.status}</p>
      <p>üèÜ Toss Winner: {match.toss_winner} (Decision: {match.toss_decision})</p>
      <p>üèÜ Match Winner: {match.match_winner}</p>

      {/* ‚úÖ Batting Scorecard */}
      <Scorecard
        homeTeam={match.batting_scorecard.home_team}
        awayTeam={match.batting_scorecard.away_team}
        type="batting"
      />

      {/* ‚úÖ Bowling Scorecard */}
      <Scorecard
        homeTeam={match.bowling_scorecard.home_team}
        awayTeam={match.bowling_scorecard.away_team}
        type="bowling"
      />

      {/* ‚úÖ Commentary */}
      <Commentary commentary={match.commentary} />
    </div>
  );
};

export default MatchDetails;
