import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log(`üîÑ Fetching match details for ${matchId}...`);

    axios.get(`http://localhost:5001/api/match/${matchId}`)
      .then((response) => {
        console.log("‚úÖ Match Details:", response.data);
        setMatchDetails(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(`‚ùå Error fetching match ${matchId}:`, error);
        setLoading(false);
      });
  }, [matchId]);

  if (loading) return <p>Loading match details...</p>;
  if (!matchDetails) return <p>Match details not available.</p>;

  return (
    <div className="container">
      <h2>{matchDetails.sport_event.season.name}</h2>
      <p>Status: {matchDetails.sport_event_status.status}</p>
      <p>Score: {matchDetails.sport_event_status.display_score}</p>
    </div>
  );
};

export default MatchPage;
