import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-31";
            else if (activeTab === "recent") date = "2025-01-30";
            else if (activeTab === "upcoming") date = "2025-02-01";

            try {
                console.log(`üü° Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("‚úÖ API Response:", response.data);
                setMatches(response.data);
            } catch (error) {
                console.error("‚ùå Error fetching matches:", error.message);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => setActiveTab("current")}>Current</button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => setActiveTab("recent")}>Recent</button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => setActiveTab("upcoming")}>Upcoming</button>
            </div>
            {error ? <h3>{error}</h3> : (
                matches.length > 0 ? matches.map(match => (
                    <div key={match.id} className="match-card">
                        <Link to={`/match/${match.id}`} className="match-link">
                            {match.teamA} vs {match.teamB}
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Venue: {match.venue}</p>
                        <p>Scheduled: {match.scheduled}</p>
                        <p>Status: {match.status}</p>
                        <p>Toss Winner: {match.toss_winner} ({match.toss_decision})</p>
                        <p>Match Winner: {match.winner}</p>
                    </div>
                )) : <h3>No matches available.</h3>
            )}
        </div>
    );
};

export default Home;
