import { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [creditRequests, setCreditRequests] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const usersRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/users`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUsers(usersRes.data);

        const requestsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCreditRequests(requestsRes.data);
      } catch (error) {
        console.error('Failed to load admin data:', error);
      }
    };

    fetchData();
  }, []);

  const handleRemoveUser = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(users.filter(user => user._id !== id));
    } catch (error) {
      console.error('Failed to remove user:', error);
    }
  };

  const handleApproveRequest = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests/${id}/approve`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCreditRequests(creditRequests.map(req =>
        req._id === id ? { ...req, status: 'approved' } : req
      ));
    } catch (error) {
      console.error('Failed to approve request:', error);
    }
  };

  const handleRejectRequest = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests/${id}/reject`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCreditRequests(creditRequests.map(req =>
        req._id === id ? { ...req, status: 'rejected' } : req
      ));
    } catch (error) {
      console.error('Failed to reject request:', error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>üèÜ Admin Dashboard</h2>

      {/* Users Table */}
      <h3>üë• Users</h3>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Phone</th>
            <th>Credits</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.phone}</td>
              <td>{user.credits}</td>
              <td>
                <button onClick={() => handleRemoveUser(user._id)} className="remove-btn">‚ùå Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Credit Requests Table */}
      <h3>üí≤ Credit Requests</h3>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Amount</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {creditRequests.map(req => (
            <tr key={req._id}>
              <td>{req.username}</td>
              <td>{req.amount}</td>
              <td>{req.status}</td>
              <td>
                {req.status === 'pending' && (
                  <>
                    <button onClick={() => handleApproveRequest(req._id)} className="approve-btn">‚úÖ Approve</button>
                    <button onClick={() => handleRejectRequest(req._id)} className="reject-btn">‚ùå Reject</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
