      {bets.map((bet) => (
        <div key={bet.id} className="bet-card">
          {bet.matchId} | {bet.team} | ðŸ’° {bet.amount} | Status: {bet.status}
        </div>
      ))}
    </div>
  );
};

export default ProfilePage;
