import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`ðŸŸ¡ Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details.");
            }
        };
        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    return (
        <div>
            <h1>Match Details</h1>
            <h2>{matchDetails.teams.join(" vs ")}</h2>
            <p>League: {matchDetails.league}</p>
            <p>Scheduled: {matchDetails.scheduled}</p>
            <p>Status: {matchDetails.status}</p>
            <p>Toss: {matchDetails.toss_winner} chose to {matchDetails.toss_decision}</p>
            <h3>Live Commentary:</h3>
            <ul>
                {matchDetails.commentary.map((comment, index) => (
                    <li key={index}>{comment}</li>
                ))}
            </ul>
        </div>
    );
};

export default MatchPage;
