import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import MatchDetails from "../components/MatchDetails";
import LiveScore from "../components/LiveScore";

const Betting = () => {
  const { matchId } = useParams();
  const [bet, setBet] = useState("");
  const [result, setResult] = useState(null);

  const handleBet = (type) => {
    const random = Math.random();
    const isEven = random >= 0.5;
    setBet(type);
    setResult(isEven ? "Even Wins!" : "Odd Wins!");
  };

  return (
    <div>
      <h2>Bet on Match</h2>
      <MatchDetails matchId={matchId} />
      <LiveScore matchId={matchId} />

      <div>
        <h3>Place Your Bet</h3>
        <button onClick={() => handleBet("Even")}>Bet Even</button>
        <button onClick={() => handleBet("Odd")}>Bet Odd</button>
      </div>

      {bet && <h4>Your Bet: {bet}</h4>}
      {result && <h4>Result: {result}</h4>}
    </div>
  );
};

export default Betting;
