import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        fetchMatches("current");
    }, []);

    const fetchMatches = async (type) => {
        setActiveTab(type);
        let date = new Date();
        if (type === "recent") date.setDate(date.getDate() - 1);
        if (type === "upcoming") date.setDate(date.getDate() + 1);

        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await axios.get(`http://localhost:5001/api/matches/${formattedDate}`);
            setMatches(response.data);
        } catch (error) {
            console.error("ðŸ”´ [ERROR] Fetching Matches Failed:", error);
            setMatches([]);
        }
    };

    return (
        <div className="home-container">
            <h1>Live Matches</h1>
            <div className="tabs">
                {["current", "recent", "upcoming"].map((type) => (
                    <button
                        key={type}
                        className={activeTab === type ? "active" : ""}
                        onClick={() => fetchMatches(type)}
                    >
                        {type.charAt(0).toUpperCase() + type.slice(1)}
                    </button>
                ))}
            </div>

            {matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.id} className="match-card">
                        <Link to={`/match/${match.id}`}>
                            <h2>{match.teamA} vs {match.teamB}</h2>
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {new Date(match.scheduled_time).toLocaleString()}</p>
                        <p>Status: {match.status}</p>
                        <p>Winner: {match.winner}</p>
                        <p>Toss: {match.toss_won_by} won the toss & chose to {match.toss_decision}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
