import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./MatchPage.css"; // CSS for styling

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [timeline, setTimeline] = useState([]);

  useEffect(() => {
    // Fetch Match Summary
    axios
      .get(`http://localhost:5001/api/matches/${matchId}`)
      .then((response) => setMatchDetails(response.data))
      .catch((error) => console.error("Error fetching match details:", error));

    // Fetch Match Timeline (Ball-by-ball updates)
    axios
      .get(`http://localhost:5001/api/matches/${matchId}/timeline`)
      .then((response) => setTimeline(response.data.timeline || []))
      .catch((error) => console.error("Error fetching timeline:", error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  const { sport_event, sport_event_status } = matchDetails;
  const teams = sport_event.competitors;
  const homeTeam = teams.find((team) => team.qualifier === "home");
  const awayTeam = teams.find((team) => team.qualifier === "away");

  return (
    <div className="match-container">
      {/* Match Info */}
      <h2>{sport_event.season.name}</h2>
      <p className="status">Status: {sport_event_status.status.toUpperCase()}</p>

      {/* Team Scores */}
      <div className="teams">
        <div className="team">
          <h3>{homeTeam.name}</h3>
          <p>
            {sport_event_status.period_scores[0].display_score} ({sport_event_status.period_scores[0].display_overs} overs)
          </p>
        </div>
        <div className="vs">VS</div>
        <div className="team">
          <h3>{awayTeam.name}</h3>
          <p>
            {sport_event_status.period_scores[1]?.display_score || "Yet to Bat"} ({sport_event_status.period_scores[1]?.display_overs || 0} overs)
          </p>
        </div>
      </div>

      {/* Ball-by-Ball Commentary */}
      <div className="timeline">
        <h3>Ball-by-Ball Commentary</h3>
        {timeline.length > 0 ? (
          <ul>
            {timeline.slice(0, 10).map((event, index) => (
              <li key={index}>
                <b>Over {event.over}.{event.ball}:</b> {event.description}
              </li>
            ))}
          </ul>
        ) : (
          <p>No commentary available.</p>
        )}
      </div>
    </div>
  );
};

export default MatchPage;
