import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState("");
    const [selectedTab, setSelectedTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${selectedTab}`);
                console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data.matches);
                setMatches(response.data.matches);
            } catch (err) {
                console.error("ðŸ”´ [ERROR] Failed to load matches.", err);
                setError("Failed to load matches.");
            }
        };
        fetchMatches();
    }, [selectedTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={selectedTab === "current" ? "active" : ""} onClick={() => setSelectedTab("current")}>Current</button>
                <button className={selectedTab === "recent" ? "active" : ""} onClick={() => setSelectedTab("recent")}>Recent</button>
                <button className={selectedTab === "upcoming" ? "active" : ""} onClick={() => setSelectedTab("upcoming")}>Upcoming</button>
            </div>

            {error && <h2 className="error">{error}</h2>}

            {matches.length > 0 ? matches.map(match => (
                <div key={match.id} className="match-card">
                    <Link to={`/match/${match.id}`}>
                        <h2>{match.competitors[0]?.name} vs {match.competitors[1]?.name}</h2>
                    </Link>
                    <p>League: {match.tournament?.name || "N/A"}</p>
                    <p>Scheduled: {new Date(match.scheduled).toLocaleString()}</p>
                    <p>Status: {match.status || "N/A"}</p>
                    <p>Winner: {match.winner_id ? match.winner_id : "TBD"}</p>
                    <p>Toss: {match.toss_won_by || "TBD"}</p>
                </div>
            )) : <p>No matches available.</p>}
        </div>
    );
};

export default Home;
