import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css"; // Add styles for button highlighting

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState("current");

  useEffect(() => {
    fetchMatches("current");
  }, []);

  const fetchMatches = (type) => {
    setActiveTab(type);
    let date = new Date();
    if (type === "recent") date.setDate(date.getDate() - 1);
    if (type === "upcoming") date.setDate(date.getDate() + 1);

    const formattedDate = date.toISOString().split("T")[0];

    axios
      .get(`http://localhost:5001/api/matches/${formattedDate}`)
      .then((res) => setMatches(res.data))
      .catch((err) => console.error("Error fetching matches", err));
  };

  return (
    <div className="home-container">
      <h1>Live Matches</h1>
      <div className="tab-buttons">
        <button className={activeTab === "current" ? "active" : ""} onClick={() => fetchMatches("current")}>Current</button>
        <button className={activeTab === "recent" ? "active" : ""} onClick={() => fetchMatches("recent")}>Recent</button>
        <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => fetchMatches("upcoming")}>Upcoming</button>
      </div>

      {matches.length > 0 ? (
        matches.map((match) => (
          <div key={match.sport_event.id} className="match-card">
            <Link to={`/match/${match.sport_event.id}`}>
              <h2>{match.sport_event.competitors?.[0]?.name} vs {match.sport_event.competitors?.[1]?.name}</h2>
            </Link>
            <p>Status: {match.sport_event_status.status}</p>
            <p>Winner: {match.sport_event.competitors?.find(team => team.id === match.sport_event_status.winner_id)?.name || "TBD"}</p>
          </div>
        ))
      ) : (
        <p>No matches available.</p>
      )}
    </div>
  );
};

export default Home;
