import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [activeTab, setActiveTab] = useState("scorecard");

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                console.error("ðŸ”´ [ERROR] Could not fetch match details:", err);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    return matchDetails ? (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.sport_event.competitors[0].name} vs {matchDetails.sport_event.competitors[1].name}</h2>
            <p>League: {matchDetails.sport_event.tournament?.name}</p>

            <div className="tabs">
                <button onClick={() => setActiveTab("scorecard")}>Scorecard</button>
                <button onClick={() => setActiveTab("commentary")}>Commentary</button>
            </div>

            {activeTab === "scorecard" ? <p>Scorecard Data Here</p> : <p>Commentary Data Here</p>}
        </div>
    ) : <h2>Loading match details...</h2>;
};

export default MatchPage;
