import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${activeTab}`);
                console.log(`üü¢ [FRONTEND] ${activeTab.toUpperCase()} Matches received:`, response.data);
                setMatches(response.data);
                setError(null);
            } catch (err) {
                console.error(`‚ùå [ERROR] Could not fetch ${activeTab} matches:`, err.message);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>

            {/* ‚úÖ FIX: Add missing tabs */}
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => setActiveTab("current")}>
                    Current
                </button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => setActiveTab("recent")}>
                    Recent
                </button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => setActiveTab("upcoming")}>
                    Upcoming
                </button>
            </div>

            {error && <h2>{error}</h2>}

            {matches.length > 0 ? (
                matches.map((match, index) => (
                    <div key={index} className="match-card">
                        <Link to={`/match/${match.id}`}>
                            <h2>{match.teamA} vs {match.teamB}</h2>
                        </Link>
                        <p>League: {match.league || "N/A"}</p>
                        <p>Scheduled: {match.scheduled ? new Date(match.scheduled).toLocaleString() : "N/A"}</p>
                        <p>Status: {match.status || "N/A"}</p>
                        <p>Winner: {match.winner || "TBD"}</p>
                        <p>Toss: {match.toss_won_by ? `${match.toss_won_by} chose to ${match.toss_decision}` : "TBD"}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
