import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles.css"; // Ensure correct import

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        fetchMatches("current");
    }, []);

    const fetchMatches = async (type) => {
        setActiveTab(type);
        let date = new Date();
        if (type === "recent") date.setDate(date.getDate() - 1);
        if (type === "upcoming") date.setDate(date.getDate() + 1);

        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await axios.get(`http://localhost:5000/api/matches/${formattedDate}`);
            console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data);

            setMatches(response.data);
        } catch (error) {
            console.error("ðŸ”´ [ERROR] Could not fetch matches:", error);
            setMatches([]);
        }
    };

    return (
        <div>
            <h1>Live Matches</h1>
            <div className="tab-container">
                {["current", "recent", "upcoming"].map((tab) => (
                    <button
                        key={tab}
                        className={activeTab === tab ? "active-tab" : ""}
                        onClick={() => fetchMatches(tab)}
                    >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>

            {matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.sport_event.id} className="match-card">
                        <Link to={`/match/${match.sport_event.id}`}>
                            <h2>{match.teamA || "Unknown"} vs {match.teamB || "Unknown"}</h2>
                        </Link>
                        <p>League: {match.sport_event.season.name}</p>
                        <p>Scheduled: {new Date(match.sport_event.scheduled).toLocaleString()}</p>
                        <p>Status: {match.sport_event.status}</p>
                        <p>Winner: {match.sport_event_status.winner_id || "Unknown"}</p>
                        <p>Toss: {match.sport_event_status.toss_won_by || "Unknown"} chose to {match.sport_event_status.toss_decision}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
