import React, { useState } from "react";

function BettingSection({ matchId }) {
    const [bet, setBet] = useState(null);

    const placeBet = (betType) => {
        setBet(betType);
        alert(`You placed a ${betType} bet on Match ID: ${matchId}`);
    };

    return (
        <div>
            <h3>Place Your Bet</h3>
            <button className="btn btn-success me-2" onClick={() => placeBet("even")}>Bet Even</button>
            <button className="btn btn-danger" onClick={() => placeBet("odd")}>Bet Odd</button>
            {bet && <p>You placed a {bet} bet.</p>}
        </div>
    );
}

export default BettingSection;
