import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [timeline, setTimeline] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5001/api/match/${matchId}`)
      .then(response => setMatchDetails(response.data))
      .catch(error => console.error("Error fetching match details:", error));

    axios.get(`http://localhost:5001/api/match/${matchId}/timeline`)
      .then(response => setTimeline(response.data.timeline || []))
      .catch(error => console.error("Error fetching timeline:", error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div className="container">
      <h2>{matchDetails.sport_event.season.name}</h2>
      <p>Status: {matchDetails.sport_event_status.status}</p>

      <div className="score-container">
        <div className="team">
          <h3>{matchDetails.sport_event.competitors[0].name}</h3>
          <p>{matchDetails.sport_event_status.period_scores[0].display_score} ({matchDetails.sport_event_status.period_scores[0].display_overs} overs)</p>
        </div>
        <p className="vs">VS</p>
        <div className="team">
          <h3>{matchDetails.sport_event.competitors[1].name}</h3>
          <p>{matchDetails.sport_event_status.period_scores[1].display_score} ({matchDetails.sport_event_status.period_scores[1].display_overs} overs)</p>
        </div>
      </div>

      <h3>Ball-by-Ball Commentary</h3>
      <ul>
        {timeline.length ? (
          timeline.map((event, index) => (
            <li key={index}>{event.text}</li>
          ))
        ) : (
          <p>No commentary available.</p>
        )}
      </ul>
    </div>
  );
};

export default MatchPage;
