import React from "react";

const MatchDetails = ({ matchDetails }) => {
  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div>
      <h2>Match Scorecard</h2>
      <div className="team-score">
        <p>{matchDetails.sport_event.competitors[0].name} - {matchDetails.sport_event_status.period_scores[0].display_score}</p>
        <span className="vs">VS</span>
        <p>{matchDetails.sport_event.competitors[1].name} - {matchDetails.sport_event_status.period_scores[1].display_score}</p>
      </div>

      <h3>Match Highlights</h3>
      <p>Winner: {matchDetails.sport_event_status.winner_id ? matchDetails.sport_event.competitors.find(team => team.id === matchDetails.sport_event_status.winner_id)?.name : "N/A"}</p>
      <p>Match Status: {matchDetails.sport_event_status.status}</p>
      <p>Overs: {matchDetails.sport_event_status.display_overs}</p>
    </div>
  );
};

export default MatchDetails;
