import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const date = "2025-01-30"; // Change dynamically as needed
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("üü¢ [FRONTEND] Matches received:", response.data);
                setMatches(response.data);
            } catch (err) {
                console.error("‚ùå [ERROR] Could not fetch matches:", err.message);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, []);

    return (
        <div>
            <h1>Live Matches</h1>
            {error && <h2>{error}</h2>}

            {matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.id} className="match-card">
                        <Link to={`/match/${match.id}`}>
                            <h2>{match.teamA} vs {match.teamB}</h2>
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {new Date(match.scheduled).toLocaleString()}</p>
                        <p>Status: {match.status}</p>
                        <p>Winner: {match.winner}</p>
                        <p>Toss: {match.toss_won_by} chose to {match.toss_decision}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
