import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMatchDetails } from '../api/apiService';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';
import '../styles/pages/MatchDetailsPage.css';

const MatchDetailsPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const data = await getMatchDetails(matchId);
        console.log('Fetched match details:', data);
        setMatchDetails(data);
      } catch (error) {
        console.error('Failed to fetch match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div className="match-details-container">
      <h2>{matchDetails.league} - {matchDetails.home_team} vs {matchDetails.away_team}</h2>
      <p>üìÖ Date: {new Date(matchDetails.scheduled).toLocaleString()}</p>
      <p>üìç Venue: {matchDetails.venue || 'TBD'}</p>
      <p>üî• Status: {matchDetails.status}</p>

      {/* ‚úÖ Pass Data to Components */}
      <Scorecard
        battingScorecard={matchDetails.batting_scorecard}
        bowlingScorecard={matchDetails.bowling_scorecard}
      />
      <Commentary commentary={matchDetails.commentary} />
    </div>
  );
};

export default MatchDetailsPage;
