import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [matches, setMatches] = useState([]); // ðŸ”¥ Default to empty array

  useEffect(() => {
    axios
      .get("http://localhost:5001/api/matches") // Ensure backend is running
      .then((response) => {
        console.log("API Response:", response.data); // Debugging Log
        if (Array.isArray(response.data)) {
          setMatches(response.data);
        } else if (response.data && Array.isArray(response.data.results)) {
          setMatches(response.data.results); // Adjust if matches are inside `results`
        } else {
          console.error("Unexpected API response format:", response.data);
          setMatches([]); // Fallback to prevent crash
        }
      })
      .catch((error) => {
        console.error("Error fetching matches:", error);
        setMatches([]); // Set empty array on failure
      });
  }, []);

  return (
    <div>
      <h1>Live Matches</h1>
      {matches.length === 0 ? (
        <p>No live matches available.</p>
      ) : (
        <ul>
          {matches.map((match) => (
            <li key={match.sport_event.id}>
              <Link to={`/match/${match.sport_event.id}`}>
                {match.sport_event.season.name} - {match.sport_event_status.display_score}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Home;
