import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        fetchMatchDetails();
    }, [matchId]);

    const fetchMatchDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/match/${matchId}`);
            console.log("ðŸŸ¢ [FRONTEND] Match details received:", response.data);
            setMatchDetails(response.data);
        } catch (err) {
            console.error("ðŸ”´ [ERROR] Failed to fetch match details:", err);
            setError("Failed to load match details.");
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <h2>Loading match details...</h2>;
    if (error) return <h2>{error}</h2>;

    return (
        <div className="match-detail">
            <h1>{matchDetails.teamA || "Unknown"} vs {matchDetails.teamB || "Unknown"}</h1>
            <p>League: {matchDetails.league}</p>
            <p>Scheduled: {new Date(matchDetails.scheduled).toLocaleString()}</p>
            <p>Status: {matchDetails.status}</p>
            <p>Winner: {matchDetails.winner || "Unknown"}</p>
            <p>Toss: {matchDetails.toss_won_by || "Unknown"} won the toss and chose to {matchDetails.toss_decision}</p>
        </div>
    );
};

export default MatchPage;
