import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css"
const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-30";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-01-31";

            console.log(`üü° Fetching matches for ${activeTab}: ${date}`);

            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("‚úÖ API Response:", response.data);
                setMatches(response.data);
            } catch (err) {
                console.error("‚ùå Error fetching matches:", err);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <button onClick={() => setActiveTab("current")}>Current</button>
            <button onClick={() => setActiveTab("recent")}>Recent</button>
            <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>

            {error && <h2>{error}</h2>}

            {matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.id}>
                        <Link to={`/match/${match.id}`}>
                            {match.sport_event.competitors[0].name} vs {match.sport_event.competitors[1].name}
                        </Link>
                        <p>League: {match.sport_event.season?.name || "N/A"}</p>
                        <p>Scheduled: {match.sport_event.scheduled}</p>
                        <p>Status: {match.sport_event_status?.status || "N/A"}</p>
                    </div>
                ))
            ) : (
                <h3>No matches available.</h3>
            )}
        </div>
    );
};

export default Home;
