import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details");
                console.error("‚ùå Error fetching match details:", err);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    const { competitors, league, venue, scheduled, status, toss_winner, toss_decision, winner, scores } = matchDetails;

    // ‚úÖ Mapping Competitor IDs to Names
    const teamMap = {};
    competitors?.forEach(team => {
        teamMap[team.id] = team.name;
    });

    // ‚úÖ Assign Proper Team Names
    const teamA = teamMap[competitors?.[0]?.id] || "Unknown Team A";
    const teamB = teamMap[competitors?.[1]?.id] || "Unknown Team B";

    // ‚úÖ Assign Toss Winner & Match Winner Correctly
    const tossWinner = teamMap[toss_winner] || "Unknown";
    const matchWinner = teamMap[winner] || "N/A";

    // ‚úÖ Assign Scores Properly
    let teamAScore = "N/A", teamBScore = "N/A";
    if (scores && scores.length > 0) {
        if (scores.length === 2) {
            teamBScore = scores[0].display_score; // Away Team (First Inning)
            teamAScore = scores[1].display_score; // Home Team (Second Inning)
        }
    }

    return (
        <div className="match-details">
            <h1>Match Details</h1>
            <h2>{teamA} vs {teamB}</h2>
            <p><strong>League:</strong> {league}</p>
            <p><strong>Venue:</strong> {venue || "Unknown"}</p>
            <p><strong>Scheduled:</strong> {scheduled}</p>
            <p><strong>Status:</strong> {status}</p>
            <p><strong>Toss Winner:</strong> {tossWinner} ({toss_decision})</p>
            <p><strong>Match Winner:</strong> {matchWinner}</p>

            <h3>Scores:</h3>
            <p><strong>{teamA}:</strong> {teamAScore}</p>
            <p><strong>{teamB}:</strong> {teamBScore}</p>
        </div>
    );
};

export default MatchPage;
