import { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/pages/AdminDashboard.css';
import { useAuth } from '../context/AuthContext';

const AdminDashboard = () => {
  const { user, isAdmin } = useAuth();

  const [users, setUsers] = useState([]);
  const [creditRequests, setCreditRequests] = useState([]);
  const [bets, setBets] = useState([]);

  useEffect(() => {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  if (token && role === 'admin') {
    fetchUsers();
    fetchCreditRequests();
    fetchBets();
  }
}, []);


  // ‚úÖ Fetch all users
  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/users`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(data);
    } catch (error) {
      console.error('Failed to load users:', error);
    }
  };

  // ‚úÖ Fetch all credit requests
  const fetchCreditRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCreditRequests(data);
    } catch (error) {
      console.error('Failed to load credit requests:', error);
    }
  };

  // ‚úÖ Fetch all bets
  const fetchBets = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/bets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBets(data);
    } catch (error) {
      console.error('Failed to load bets:', error);
    }
  };

  // ‚úÖ Handle Approve/Reject Credit Request
  const handleCreditAction = async (id, status, reason = '') => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/admin/credit-requests/${id}`,
        { status, reason },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // ‚úÖ Update State Directly After Action
      setCreditRequests((prev) =>
        prev.map((req) =>
          req._id === data.request._id ? { ...req, ...data.request } : req
        )
      );
    } catch (error) {
      console.error(`Failed to ${status} credit request:`, error);
    }
  };

  // ‚úÖ Delete User
  const handleDeleteUser = async (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/admin/users/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // ‚úÖ Update State Directly After Action
        setUsers((prev) => prev.filter((user) => user._id !== id));
      } catch (error) {
        console.error('Failed to delete user:', error);
      }
    }
  };

  // ‚úÖ Delete Bet
  const handleDeleteBet = async (id) => {
    if (window.confirm('Are you sure you want to delete this bet?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/admin/bets/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // ‚úÖ Update State Directly After Action
        setBets((prev) => prev.filter((bet) => bet._id !== id));
      } catch (error) {
        console.error('Failed to delete bet:', error);
      }
    }
  };

  if (!isAdmin) {
    return <div>‚ùå Access Denied</div>;
  }

  return (
    <div className="admin-dashboard">
      <h2>üèÜ Admin Dashboard</h2>

      {/* ‚úÖ User List */}
      <section>
        <h3>üë• Users</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Phone</th>
              <th>Credits</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.username}</td>
                <td>{user.phone}</td>
                <td>{user.credits}</td>
                <td>
                  <button onClick={() => handleDeleteUser(user._id)}>‚ùå Remove</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* ‚úÖ Credit Requests */}
      <section>
        <h3>üí∞ Credit Requests</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Amount</th>
              <th>Phone</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {creditRequests.map(request => (
              <tr key={request._id}>
                <td>{request.username}</td>
                <td>{request.amount}</td>
                <td>{request.phone}</td>
                <td>{request.status}</td>

                <td>
                  {request.status === 'pending' && (
                    <>
                      <button onClick={() => handleCreditAction(request._id, 'approve')}>‚úÖ Approve</button>
                      <button onClick={() => handleCreditAction(request._id, 'reject')}>‚ùå Reject</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* ‚úÖ Bets */}
      <section>
        <h3>üìä Bets</h3>
        <table>
          <thead>
            <tr>
              <th>Match</th>
              <th>Username</th>
              <th>Amount</th>
              <th>Team</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bets.map(bet => (
              <tr key={bet._id}>
                <td>{bet.matchId}</td>
                <td>{bet.username}</td>
                <td>{bet.amount}</td>
                <td>{bet.team}</td>
                <td>{bet.status}</td>
                <td>
                  <button onClick={() => handleDeleteBet(bet._id)}>‚ùå Cancel Bet</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
