import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMatchDetails } from '../api/apiService';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';
import '../styles/pages/MatchDetailsPage.css';

const MatchDetailsPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const data = await getMatchDetails(matchId);
        setMatchDetails(data);
      } catch (error) {
        console.error('Failed to fetch match details:', error);
      }
    };

    if (matchId) {
      fetchMatchDetails();
    }
  }, [matchId]);

  if (!matchDetails) {
    return <div>Loading match details...</div>;
  }

  return (
    <div className="match-details-container">
      <h1>{matchDetails.league} - {matchDetails.home_team} vs {matchDetails.away_team}</h1>
      <p>üìÖ <strong>Date:</strong> {new Date(matchDetails.scheduled).toLocaleString()}</p>
      <p>üìç <strong>Venue:</strong> {matchDetails.venue || 'TBD'}</p>
      <p>üî• <strong>Status:</strong> {matchDetails.status}</p>

      {/* ‚úÖ Batting Scorecard */}
      <Scorecard
        title={`${matchDetails.home_team} Batting`}
        data={matchDetails.batting_scorecard?.home_team}
      />
      <Scorecard
        title={`${matchDetails.away_team} Batting`}
        data={matchDetails.batting_scorecard?.away_team}
      />

      {/* ‚úÖ Bowling Scorecard */}
      <Scorecard
        title={`${matchDetails.home_team} Bowling`}
        data={matchDetails.bowling_scorecard?.home_team}
      />
      <Scorecard
        title={`${matchDetails.away_team} Bowling`}
        data={matchDetails.bowling_scorecard?.away_team}
      />

      {/* ‚úÖ Commentary */}
      <Commentary commentary={matchDetails.commentary} />
    </div>
  );
};

export default MatchDetailsPage;
