import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedTab, setSelectedTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${selectedTab}`);
                setMatches(response.data);
                setError(null);
            } catch (err) {
                console.error("❌ Error fetching matches:", err);
                setError("Failed to load matches.");
            } finally {
                setLoading(false);
            }
        };

        fetchMatches();
    }, [selectedTab]);

    return (
        <div className="container">
            <h1>Live Matches</h1>
            <div className="tabs">
                <button onClick={() => setSelectedTab("current")}>Current</button>
                <button onClick={() => setSelectedTab("recent")}>Recent</button>
                <button onClick={() => setSelectedTab("upcoming")}>Upcoming</button>
            </div>

            {loading && <p>Loading matches...</p>}
            {error && <p>{error}</p>}

            {matches.map((match) => (
                <div key={match.id} className="match-card">
                    <Link to={`/match/${match.id}`}>
                        <h2>{match.competitors[0].name} vs {match.competitors[1].name}</h2>
                    </Link>
                    <p>League: {match.league?.name || "N/A"}</p>
                    <p>Scheduled: {new Date(match.scheduled).toLocaleString()}</p>
                    <p>Status: {match.status}</p>
                    <p>Winner: {match.winner?.name || "TBD"}</p>
                    <p>Toss: {match.toss?.winner.name || "TBD"} chose to {match.toss?.decision || "TBD"}</p>
                </div>
            ))}
        </div>
    );
};

export default Home;
