import axios from "axios";

// ✅ Only use .env config
const API_URL = process.env.REACT_APP_API_URL + "/user";

// ✅ Register User
export const registerUser = async (userData) => {
  console.log("Sending registration payload:", userData);
  try {
    const { data } = await axios.post(`${API_URL}/register`, userData);
    console.log("Registration success:", data);
    return data;
  } catch (error) {
    console.error("Registration failed:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Login User
export const loginUser = async (userData) => {
  console.log("Sending login payload:", userData);
  try {
    const { data } = await axios.post(`${API_URL}/login`, userData);

    if (data.token) {
      localStorage.setItem("token", data.token);
    }

    console.log("Login success:", data);
    return data;
  } catch (error) {
    console.error("Login failed:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Logout User
export const logoutUser = async () => {
  console.log("Sending logout request");

  try {
    const token = localStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    await axios.post(`${API_URL}/logout`, {}, config);
    localStorage.removeItem("token");
  } catch (error) {
    console.error("Logout failed:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Get User Profile
export const getProfile = async () => {
  console.log("Fetching user profile");

  try {
    const token = localStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const { data } = await axios.get(`${API_URL}/profile`, config);
    console.log("Profile loaded:", data);
    return data;
  } catch (error) {
    console.error("Failed to load user profile:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Request Credits
export const requestCredits = async (amount) => {
  try {
    const token = localStorage.getItem("token");
    const { data } = await axios.post(
      `${API_URL}/request-credits`,
      { amount },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    return data;
  } catch (error) {
    throw error.response?.data?.error || "Failed to request credits";
  }
};
