import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/pages/AdminDashboard.css";
import { useAuth } from "../context/AuthContext";

const AdminDashboard = () => {
  const { user, isAdmin } = useAuth();
  const [users, setUsers] = useState([]);
  const [creditRequests, setCreditRequests] = useState([]);
  const [bets, setBets] = useState([]);

  useEffect(() => {
    if (isAdmin) {
      fetchUsers();
      fetchCreditRequests();
      fetchBets();
    }
  }, [isAdmin]);

  const fetchUsers = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/admin/users`
      );
      setUsers(data);
    } catch (error) {
      console.error("Failed to fetch users:", error);
    }
  };

  const fetchCreditRequests = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/admin/credit-requests`
      );
      setCreditRequests(data);
    } catch (error) {
      console.error("Failed to fetch credit requests:", error);
    }
  };

  const fetchBets = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/admin/bets`
      );
      setBets(data);
    } catch (error) {
      console.error("Failed to fetch bets:", error);
    }
  };

  const handleRemoveUser = async (userId) => {
    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/admin/users/${userId}`
      );
      setUsers((prev) => prev.filter((user) => user._id !== userId));
    } catch (error) {
      console.error("Failed to remove user:", error);
    }
  };

  const handleApprove = async (requestId) => {
    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/admin/approve-credit/${requestId}`
      );
      fetchCreditRequests(); // Refresh the list
    } catch (error) {
      console.error("Failed to approve credit:", error);
    }
  };

  const handleReject = async (requestId) => {
    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/admin/reject-credit/${requestId}`
      );
      fetchCreditRequests(); // Refresh the list
    } catch (error) {
      console.error("Failed to reject credit:", error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>🏆 Admin Dashboard</h2>

      {/* USERS TABLE */}
      <div className="table-container">
        <h3>👥 Users</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Phone</th>
              <th>Credits</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u._id}>
                <td>{u.username}</td>
                <td>{u.phone}</td>
                <td>{u.credits}</td>
                <td>
                  <button
                    onClick={() => handleRemoveUser(u._id)}
                    className="remove-btn"
                  >
                    ❌ Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* CREDIT REQUESTS TABLE */}
      <div className="table-container">
        <h3>💰 Credit Requests</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Phone</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Reason</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {creditRequests.map((c) => (
              <tr key={c._id}>
                <td>{c.username}</td>
                <td>{c.phone}</td>
                <td>{c.amount}</td>
                <td>{c.status}</td>
                <td>{c.reason || "N/A"}</td>
                <td>
                  {c.status === "pending" && (
                    <>
                      <button
                        onClick={() => handleApprove(c._id)}
                        className="approve-btn"
                      >
                        ✅
                      </button>
                      <button
                        onClick={() => handleReject(c._id)}
                        className="reject-btn"
                      >
                        ❌
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* BETS TABLE */}
      <div className="table-container">
        <h3>📊 Bets</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Match</th>
              <th>Amount</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bets.map((b) => (
              <tr key={b._id}>
                <td>{b.username}</td>
                <td>{b.matchId}</td>
                <td>{b.amount}</td>
                <td>{b.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
