import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/pages/BettingPage.css';
import { useAuth } from '../context/AuthContext';

const BettingPage = () => {
  const { matchId } = useParams();
  const { user } = useAuth();
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleBet = async () => {
    if (!amount || isNaN(amount) || amount <= 0) {
      alert('Please enter a valid amount');
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/bet/place`,
        {
          matchId,
          amount: parseFloat(amount),
          team: 'home', // You can modify this as per your requirement
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert('Bet placed successfully!');
      navigate('/home'); // âœ… Redirect after successful bet
    } catch (error) {
      console.error('Failed to place bet:', error);
      alert('Failed to place bet. Try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="betting-page">
      <h2>ðŸ’¸ Place Your Bet</h2>

      {/* âœ… Amount Input */}
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Enter Amount"
        className="bet-input"
      />

      {/* âœ… Bet Now Button */}
      <button onClick={handleBet} className="bet-now-btn" disabled={loading}>
        {loading ? 'Placing...' : 'ðŸ’° Bet Now'}
      </button>
    </div>
  );
};

export default BettingPage;
