import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';
import '../styles/pages/MatchDetailsPage.css';

const MatchDetailPage = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);
  const [activeTab, setActiveTab] = useState('scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/match/${matchId}`);
        const data = await response.json();
        setMatch(data);
      } catch (error) {
        console.error('Failed to fetch match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!match) {
    return <div>Loading match details...</div>;
  }

  const {
    home_team,
    away_team,
    start_date,
    venue,
    status,
    toss,
    match_winner,
    home_score,
    away_score,
    batting_scorecard,
    bowling_scorecard,
    commentary
  } = match;

  return (
    <div className="match-detail-container">
      {/* ‚úÖ Match Header */}
      <div className="match-header">
        <h2>{home_team || '-'} vs {away_team || '-'}</h2>
        <p>üìÖ <strong>Date:</strong> {start_date ? new Date(start_date).toLocaleString() : 'Invalid Date'}</p>
        <p>üìç <strong>Venue:</strong> {venue || 'Unknown Venue'}</p>
        <p>üî• <strong>Status:</strong> {status || '-'}</p>
        {toss && <p>üèÜ <strong>Toss:</strong> {toss}</p>}
        {match_winner && <p>ü•á <strong>Winner:</strong> {match_winner}</p>}
        <p>üèè <strong>Score:</strong> {home_score || '-'} - {away_score || '-'}</p>
      </div>

      {/* ‚úÖ Tab Switcher */}
      <div className="tab-container">
        <button
          className={`tab ${activeTab === 'scorecard' ? 'active' : ''}`}
          onClick={() => setActiveTab('scorecard')}
        >
          Scorecard
        </button>
        <button
          className={`tab ${activeTab === 'commentary' ? 'active' : ''}`}
          onClick={() => setActiveTab('commentary')}
        >
          Commentary
        </button>
      </div>

      {/* ‚úÖ Render Based on Active Tab */}
      {activeTab === 'scorecard' ? (
        <Scorecard
          battingScorecard={batting_scorecard}
          bowlingScorecard={bowling_scorecard}
          homeTeam={home_team}
          awayTeam={away_team}
        />
      ) : (
        <Commentary commentary={commentary} />
      )}
    </div>
  );
};

export default MatchDetailPage;
