import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-30";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-01-31";

            try {
                console.log(`üü° Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                setMatches(response.data);
            } catch (err) {
                console.error("‚ùå [ERROR] Could not fetch matches:", err);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div className="container">
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => setActiveTab("current")}>Current</button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => setActiveTab("recent")}>Recent</button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => setActiveTab("upcoming")}>Upcoming</button>
            </div>

            {error ? (
                <h2>{error}</h2>
            ) : (
                matches.map((match, index) => (
                    <div key={index} className="match-card">
                        <Link to={`/match/${match.sport_event.id}`}>
                            <h2>{match.sport_event.competitors[0].name} vs {match.sport_event.competitors[1].name}</h2>
                        </Link>
                        <p>League: {match.sport_event.season.name || "N/A"}</p>
                        <p>Scheduled: {match.sport_event.scheduled}</p>
                        <p>Status: {match.sport_event_status.status || "N/A"}</p>
                        <p>Winner: {match.sport_event_status.winner_id || "TBD"}</p>
                        <p>Toss: {match.sport_event_status.toss_won_by || "TBD"} chose to {match.sport_event_status.toss_decision || "TBD"}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default Home;
