import React from 'react';
import '../styles/components/Commentary.css';

const Commentary = ({ commentary }) => {
  if (!commentary || commentary.length === 0) {
    return <p>No commentary available.</p>;
  }

  return (
    <div className="commentary">
      <h2>üó£Ô∏è Commentary</h2>
      {commentary.map((entry, index) => (
        <div key={index} className="commentary-entry">
          <p>
            <strong>Over:</strong> {entry.over}, <strong>Ball:</strong> {entry.ball}
          </p>
          <p>
            <strong>Batsman:</strong> {entry.batsman} | <strong>Bowler:</strong> {entry.bowler}
          </p>
          <p>
            <strong>Runs:</strong> {entry.runs}
          </p>
          <p>{entry.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Commentary;
