import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const date = new Date().toISOString().split("T")[0];
                console.log(`ðŸŸ¡ Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);

                console.log("âœ… API Response:", response.data);
                setMatches(response.data);
            } catch (err) {
                setError("Failed to fetch matches.");
            }
        };
        fetchMatches();
    }, [activeTab]);

    return (
        <div className="match-container">
            <h1>Live Matches</h1>
            <div className="tabs">
                {["Current", "Recent", "Upcoming"].map(tab => (
                    <button key={tab} className={activeTab === tab.toLowerCase() ? "active" : ""}
                        onClick={() => setActiveTab(tab.toLowerCase())}>
                        {tab}
                    </button>
                ))}
            </div>

            {error ? <h3>{error}</h3> :
                matches.length > 0 ? matches.map(match => (
                    <div className="match-card" key={match.id}>
                        <Link to={`/match/${match.id}`}>
                            {match.teamA} vs {match.teamB}
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {match.scheduled}</p>
                        <p>Status: {match.status}</p>
                    </div>
                )) : <h3>No matches available.</h3>
            }
        </div>
    );
};

export default Home;
