import { useEffect, useState } from 'react';
import { getLiveMatches, getMatchesByDate } from '../api/matchService';
import { useNavigate } from 'react-router-dom';
import '../styles/pages/HomePage.css';

const HomePage = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState('live');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        let data;
        if (activeTab === 'live') {
          data = await getLiveMatches(); // ‚úÖ Load live matches
        } else {
          const date = activeTab === 'recent'
            ? new Date(new Date().setDate(new Date().getDate() - 1)).toISOString().split('T')[0]
            : new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().split('T')[0];
          data = await getMatchesByDate(date);
        }
        setMatches(data);
      } catch (error) {
        console.error('Failed to fetch matches:', error);
      }
    };

    fetchMatches();
  }, [activeTab]);

  return (
    <div className="home-container">
      <h2>üèè Live Matches</h2>

      {/* ‚úÖ Tab Switcher */}
      <div className="tab-container">
        <button
          className={`tab ${activeTab === 'live' ? 'active' : ''}`}
          onClick={() => setActiveTab('live')}
        >
          Live Matches
        </button>
        <button
          className={`tab ${activeTab === 'recent' ? 'active' : ''}`}
          onClick={() => setActiveTab('recent')}
        >
          Recent Matches
        </button>
        <button
          className={`tab ${activeTab === 'upcoming' ? 'active' : ''}`}
          onClick={() => setActiveTab('upcoming')}
        >
          Upcoming Matches
        </button>
      </div>

      {/* ‚úÖ Display Matches */}
      <div className="match-list">
        {matches.length > 0 ? (
          matches.map((match) => (
            <div key={match._id} className="match-card">
              <h3>{match.league} - {match.teamA} vs {match.teamB}</h3>
              <p>Status: {match.status}</p>
              <p>Score: {match.teamAScore} - {match.teamBScore}</p>

              <div className="button-group">
                <button onClick={() => navigate(`/match/${match._id}`)}>
                  Match Details
                </button>
                <button onClick={() => navigate(`/betting/${match._id}`)}>
                  Bet Now
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>No matches available</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
