import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";


const Home = () => {
    const [matches, setMatches] = useState([]); // Default to empty array
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-30";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-01-31";

            console.log(`üü° Fetching matches for ${activeTab}: ${date}`);

            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("‚úÖ API Response:", response.data);

                if (Array.isArray(response.data)) {
                    setMatches(response.data);
                } else {
                    console.error("‚ùå Unexpected API response format", response.data);
                    setMatches([]);
                    setError("Invalid data received from API.");
                }
            } catch (err) {
                console.error("‚ùå Error fetching matches:", err);
                setError("Failed to load matches.");
            } finally {
                setLoading(false);
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <button onClick={() => setActiveTab("current")}>Current</button>
            <button onClick={() => setActiveTab("recent")}>Recent</button>
            <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>

            {loading && <h3>Loading matches...</h3>}
            {error && <h2>{error}</h2>}

            {!loading && matches.length > 0 ? (
                matches.map((match, index) => (
                    <div key={match.id || index}>
                        <Link to={`/match/${match.id}`}>
                            {match.sport_event?.competitors?.[0]?.name || "Team A"} vs{" "}
                            {match.sport_event?.competitors?.[1]?.name || "Team B"}
                        </Link>
                        <p>League: {match.sport_event?.season?.name || "N/A"}</p>
                        <p>Scheduled: {match.sport_event?.scheduled || "Unknown"}</p>
                        <p>Status: {match.sport_event_status?.status || "N/A"}</p>
                    </div>
                ))
            ) : (
                !loading && <h3>No matches available.</h3>
            )}
        </div>
    );
};

export default Home;
