import axios from "axios";

const API_URL = "/api/user";

// ✅ Register User
export const registerUser = async (username, phone, password) => {
  const { data } = await axios.post(`${API_URL}/register`, {
    username,
    phone,
    password,
  });

  return data;
};

// ✅ Login User
export const loginUser = async (username, password) => {
  const { data } = await axios.post(`${API_URL}/login`, {
    username,
    password,
  });

  // ✅ Store token in localStorage
  if (data.token) {
    localStorage.setItem("token", data.token);
  }

  return data;
};

export const logout = async () => {
  try {
    await axios.post(`${API_URL}/logout`);
    localStorage.removeItem("token");
  } catch (error) {
    throw error.response?.data?.error || "Logout failed";
  }
};

// ✅ Get User Profile
export const getProfile = async () => {
  try {
    const token = localStorage.getItem("token");
    if (!token) throw new Error("No token found");

    const { data } = await axios.get(`${API_URL}/profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    return data;
  } catch (error) {
    throw error.response?.data?.error || "Failed to load profile";
  }
};

// ✅ Request Credits
export const requestCredits = async (amount) => {
  try {
    const token = localStorage.getItem("token");
    const { data } = await axios.post(
      `${API_URL}/request-credits`,
      { amount },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    return data;
  } catch (error) {
    throw error.response?.data?.error || "Failed to request credits";
  }
};
